package study;

public class Variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;	// 리터럴
		int b=20;
		int sum=a+b;
		System.out.println(a + "+" + b + "=" + sum);
		System.out.printf("%d + %d = %d\n",a,b,sum);
		double d1 = 3.14;	// 실수 리터럴
		String data = "Hello World";	// 문자열 리터럴
		boolean flag = true;	// 참과 거짓을 저장하는 데이터 타입
			// true : 논리 리터럴 (프로그램의 일부로 상수이므로 변경 불가)
	}

}
